package com.ipreptech.management.entities;import com.ipreptech.management.enumns.Gender;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.EnumType;import javax.persistence.Enumerated;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;@Entity@Table(name = "student")@Getter@Builder@NoArgsConstructor@AllArgsConstructorpublic class Student {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "first_name")    private String firstName;    @Column(name = "last_name")    private String lastName;    @Column(name = "email")    private String email;    @Enumerated(value = EnumType.STRING)    @Column(name = "gender")    private Gender gender;}